#include <stdio.h>
#include <unistd.h>
#include <string.h>

#define MAX_KULCS 100
#define BUFFER_MERET 256

int
main (int argc, char **argv)
{

  char kulcs[MAX_KULCS];
  char buffer[BUFFER_MERET];

  int kulcs_index = 0;
  int olvasott_bajtok = 0;

  int kulcs_meret = strlen (argv[1]);
  strncpy (kulcs, argv[1], MAX_KULCS);

  while ((olvasott_bajtok = read (0, (void *) buffer, BUFFER_MERET)))
    {

      for (int i = 0; i < olvasott_bajtok; i++)
	{

	  buffer[i] = buffer[i] ^ kulcs[kulcs_index];
	  kulcs_index = (kulcs_index + 1) % kulcs_meret;

	}

      write (1, buffer, olvasott_bajtok);

    }
}

import java.io.InputStream;
import java.io.OutputStream;

public class main{
    public static void encode(String key, InputStream in, OutputStream out)
    throws java.io.IOException
    {
        byte[] thekey = key.getBytes();
        byte[] inputBuffer = new byte[256];
        int keyIndex = 0;
        int readBytes = 0;

        while((readBytes = in.read(inputBuffer)) != -1)
        {
            for(int i=0; i<readBytes; i++)
            {
                inputBuffer[i] = (byte)(inputBuffer[i] ^ thekey[keyIndex]);
                keyIndex = (keyIndex + 1) % key.length();
            }
            out.write(inputBuffer, 0, readBytes);
        }
    }

    public static void main(String[] args){
        if(args[0] != "")
        {
            try
            {
                encode(args[0], System.in, System.out);
            }
            catch(java.io.IOException e)
            {
                e.printStackTrace();
            }
        }
        else
        {
            System.out.println("Adj meg egy kulcsot!");
            System.out.println("java main <key>");
        }
    }
}

#define MAX_TITKOS 4096
#define OLVASAS_BUFFER 256
#define KULCS_MERET 4
#define _GNU_SOURCE

#include <stdio.h>
#include <unistd.h>
#include <string.h>

double
atlagos_szohossz (const char *titkos, int titkos_meret)
{
    int sz = 0;
    for (int i = 0; i < titkos_meret; ++i)
        if (titkos[i] == ' ')
            ++sz;

    return (double) titkos_meret / sz;
}

int
tiszta_lehet (const char *titkos, int titkos_meret)
{
    // a tiszta szoveg valszeg tartalmazza a gyakori magyar szavakat
    // illetve az átlagos szóhossz vizsgálatával csökkentjük a
    // potenciális töréseket

    double szohossz = atlagos_szohossz (titkos, titkos_meret);

    return szohossz > 6.0 && szohossz < 9.0;/*
           && strcasestr (titkos, "hogy") && strcasestr (titkos, "nem")
           && strcasestr (titkos, "az") && strcasestr (titkos, "ha");*/

}

void
exor (const char kulcs[], int kulcs_meret, char titkos[], int titkos_meret)
{

    int kulcs_index = 0;

    for (int i = 0; i < titkos_meret; ++i)
    {

        titkos[i] = titkos[i] ^ kulcs[kulcs_index];
        kulcs_index = (kulcs_index + 1) % kulcs_meret;

    }

}

int
exor_tores (const char kulcs[], int kulcs_meret, char titkos[],
            int titkos_meret)
{

    exor (kulcs, kulcs_meret, titkos, titkos_meret);

    return tiszta_lehet (titkos, titkos_meret);

}

int
main (void)
{

    char kulcs[KULCS_MERET];
    char titkos[MAX_TITKOS];
    char *p = titkos;
    int olvasott_bajtok;

    // titkos fajt berantasa
    while ((olvasott_bajtok =
                read (0, (void *) p,
                      (p - titkos + OLVASAS_BUFFER <
                       MAX_TITKOS) ? OLVASAS_BUFFER : titkos + MAX_TITKOS - p)))
        p += olvasott_bajtok;

    // maradek hely nullazasa a titkos bufferben
    for (int i = 0; i < MAX_TITKOS - (p - titkos); ++i)
        titkos[p - titkos + i] = '\0';
	//char str[4]= {'i', 'z', 'h', 'g'};
	char str[KULCS_MERET] = "lugn";
    // osszes kulcs eloallitasa
    for (int ii = 0; ii <= 3; ++ii)
	for (int li = 0; li <= 3; ++li)
	    for (int ki = 0; ki <= 3; ++ki)
		for (int ji = 0; ji <= 3; ++ji)
                                {
                                    kulcs[0] = str[ii];
                                    kulcs[1] = str[li];
                                    kulcs[2] = str[ki];
                                    kulcs[3] = str[ji];

                                    if (exor_tores (kulcs, KULCS_MERET, titkos, p - titkos))
                                        printf
                                        ("Kulcs: [%c%c%c%c]\nTiszta szoveg: [%s]\n",
                                         kulcs[ii], kulcs[li], kulcs[ki], kulcs[ji], titkos);

                                    // ujra EXOR-ozunk, igy nem kell egy masodik buffer
                                    exor (kulcs, KULCS_MERET, titkos, p - titkos);
                                }

    return 0;
}

#include <iostream>
#include "mlp.hpp"
#include <png++/png.hpp>

int main( int argc, char **argv)
{
	png::image <png::rgb_pixel> png_image ( argv[1] );
	int size = png_image.get_width() *png_image.get_height();

	Perceptron* p = new Perceptron ( 3, size, 256, 1);

	double* image=new double[size];
	for( int i{0}; i<png_image.get_width(); ++i)
		for( int j{0}; j<png_image.get_height(); ++j )
			image[i*png_image.get_width() +j] = png_image[i][j].red;

	double value = (*p) (image);

	std::cout<< value<< std::endl;

	delete p;
	delete [] image; 
}
