// POLARGEN.H
#ifndef POLARGEN__H
#define POLARGEN__H
#include <cstdlib>
#include <cmath>
#include <ctime>

class PolarGen
{
    public: 
        PolarGen()
        {
            nincsTarolt = true;
            std::srand (std::time (NULL));
        }
        ~PolarGen(){}
        double kovetkezo();

    private:
        bool nincsTarolt;
        double tarolt;
};

#endif

//POLARGEN.CPP
#include "polargen.h"

double PolarGen::kovetkezo()
{
    if (nincsTarolt)
    {
        double u1, u2, v1, v2, w;
        do
        {
            u1 = std::rand () / (RAND_MAX + 1.0);
            u2 = std::rand () / (RAND_MAX + 1.0);
            v1 = 2 * u1 - 1;
            v2 = 2 * u2 - 1;
            w = v1 * v1 + v2 * v2;
        } while (w > 1);
        double r = std::sqrt ((-2 * std::log (w)) / w);
        tarolt = r * v2;
        nincsTarolt = !nincsTarolt;

        return r * v1;
    }
    else
    {
         nincsTarolt = !nincsTarolt;

         return tarolt;
    }
}

#include <iostream>
#include "polargen.h"
#include "polargen.cpp"

int main(int argc, char **argv)
{
    PolarGen pg;

    for (int i = 0; i < 10; i++)
        std::cout << pg.kovetkezo() << std::endl;
}

public class Polargen {

    boolean notStored = true;
    double stored;

    public Polargen() {

        notStored = true;

    }

    public double következő() {

        if(notStored) {

            double u1, u2, v1, v2, w;
            do {
                u1 = Math.random();
                u2 = Math.random();

                v1 = 2*u1 - 1;
                v2 = 2*u2 - 1;

                w = v1*v1 + v2*v2;

            } while(w > 1);

            double r = Math.sqrt((-2*Math.log(w))/w);

            stored = r*v2;
            notStored = !notStored;

            return r*v1;

        } else {
            notStored = !notStored;
            return stored;
        }
    }

    public static void main(String[] args) {

        Polargen g = new Polargen();

        for(int i=0; i<10; ++i)
            System.out.println(g.következő());

    }
}

